mapper.select:
select (if(count(id) = 1, true, false)) as result
from authorization
where username = #{username};

select a.id, b.name
from user_roles a,
     role b
where a.role_id = b.id
  and a.authorization_id = #{id};

select a.*, b.name realName
from authorization a,
     staff_info b
where a.staff_info_id = b.id
  and username = #{username};

select b.operation_id, b.object_id
from role a,
     role_scope b
where a.id = b.role_id
  and a.id = #{id};

select distinct d.operation_id, d.object_id
from authorization a,
user_roles b,
role c,
role_scope d
where a.id = b.authorization_id
and b.role_id = c.id
and c.id = d.role_id
and a.username = #{username}

    and d.operation_id = ${operationId};
