mapper.select:
select c.*
from authentication a,
user_roles b,
role c
where a.id=b.authentication_id and
b.role_id = c.id

    and a.id = ${authenticationId}


    and a.username = #{username}

;

select
a.id as authentication_id,
a.username,
c.id as role_id,
c.name as role_name,
f.id as operation_id,
f.name as operation_name,
e.id as object_id,
e.table_name,
e.column_name
from authentication a,
user_roles b,
role c,
role_scope d,
dict_operation_object e,
dict_operation f
where a.id = b.authentication_id
and b.role_id = c.id
and c.id = d.role_id
and d.object_id = e.id
and d.operation_id = f.id

    and a.id = ${authenticationId}


    and a.username = #{username}


    and c.id = ${roleId}


    and c.name = #{roleName}


    and f.id = ${operationId}


    and f.name = #{operationName}


    and e.table_name = #{tableName}

;

select IF(count(a.id) > 0, true, false)
from authentication a, user_roles b, role c
where a.id = b.authentication_id and b.role_id = c.id

    and a.id = ${authorizationId}


    and a.username = #{username}


    and c.id = ${roleId}


    and c.name = #{roleName}

;

select IF(count(a.id) > 0, true, false)
from authentication a,
user_roles b,
role c,
role_scope d,
dict_operation_object e,
dict_operation f
where a.id = b.authentication_id
and b.role_id = c.id
and c.id = d.role_id
and d.object_id = e.id
and d.operation_id = f.id

    and a.id = ${authenticationId}


    and a.username = #{username}


    and c.id = ${roleId}


    and c.name = #{roleName}


    and f.id = ${operationId}


    and f.name = #{operationName}


    and d.object_id = ${objectId}


    and e.table_name = #{tableName}


    and e.column_name = #{column_name}

;
