mapper.select:
select

    ${field}

from staff_info
where disabled = '0'

    and (no like '%' #{query} '%'
    or name like '%' #{query} '%'
    or gender = #{query}
    or nation like '%' #{query} '%')


    limit ${from}, ${length}

;

select count(id) from staff_info where disabled = '0'
and (no like '%' #{query} '%' or name like '%' #{query} '%' or gender = #{query} or nation like '%' #{query} '%')
;

mapper.update:
update staff_info set disabled = '1'
where id = ${id}

    and no = #{no}

;

mapper.insert:
insert into staff_info(no, name, gender, nation, birth, enroll_time, politic, major, level, level_unit, level_date, job_alias, research_direction, job, department, idcard, phone, long_phone, short_phone) values
#{it.no}, #{it.name}, #{it.gender}, #{it.nation}, #{it.birth}, #{it.enrollTime}, #{it.politic}, #{it.major}, #{it.level}, #{it.levelUnit}, #{it.levelDate}, #{it.jobAlias}, #{it.researchDirection}, #{it.job}, #{it.department}, #{it.idcard}, #{it.phone}, #{it.longPhone}, #{it.shortPhone}
;

mapper.select:
select if(count(id) > 0, true, false)
from staff_info
where no = #{no};
